pragma solidity >=0.4.21 <0.7.0;

contract Flightbooking {
    string public systemName;
    address payable passengerAddress;
    //address[] public passengerBookingList;
    uint ticketPrice = 5 ether;
    uint balance;

    struct FlightBooking {
        bool isValidBooking;
        string departureCity;
        string arrivalCity;
        bool hasCheckedIn;
        address payable passenger;
    }

    mapping(address => FlightBooking) public bookings;

    //events
    event booking(
        bool isValidBooking,
        string departureCity,
        string arrivalCity,
        bool hasCheckedIn,
        address payable passenger
    );

    event chenckIn(
        bool isValidBooking,
        string departureCity,
        string arrivalCity,
        bool hasCheckedIn,
        address payable passenger
    );

    event cancelBooking(
        bool isValidBooking,
        string departureCity,
        string arrivalCity,
        bool hasCheckedIn,
        address payable passenger
    );

    event refund(
        uint transferEtherNum,
        uint totalBalanceLeft
    );

    constructor() public {
        systemName = "Chenchen Zhu Flight Booking System";
        balance = 100 ether;
    }

    //functions
    function sendEther(address payable _address, uint value) public payable {
         _address.transfer(value);
     }
    //booking
    function createBooking(string memory _departureCity, string memory _arrivalCity) public {
        passengerAddress = msg.sender;
        require(bytes(_departureCity).length > 0, "Departure city's name is required!");
        require(bytes(_arrivalCity).length > 0, "Arrival city's name is required!");
        require(bookings[passengerAddress].isValidBooking == false || bookings[passengerAddress].hasCheckedIn == true, "The passenger can not book");
        //send enough ether to the small contract
        require(balance >= ticketPrice, "Not enough Ether");
        balance = balance-5;
        sendEther(passengerAddress, ticketPrice);
        bookings[passengerAddress] = FlightBooking(true, _departureCity, _arrivalCity, false, passengerAddress);
        emit booking(true, _departureCity, _arrivalCity, false, passengerAddress);
    }


    //check in
    function checkIn() public {
        passengerAddress = msg.sender;
        require(bookings[passengerAddress].isValidBooking == true, "Can not check in if the booking is not valid");
        require(bookings[passengerAddress].hasCheckedIn == false, "Can not check in twice");
        bookings[passengerAddress].hasCheckedIn = true;
        emit chenckIn(true, bookings[passengerAddress].departureCity, bookings[passengerAddress].arrivalCity, true, passengerAddress);
    }

    function cancel() public {
        passengerAddress = msg.sender;
        require(bookings[passengerAddress].hasCheckedIn == false, "Can not cancel the ticket when you have checked in");
        bookings[passengerAddress].isValidBooking = false;
        //get the refund
        balance = balance + 5;
        emit cancelBooking(false, bookings[passengerAddress].departureCity, bookings[passengerAddress].arrivalCity, false, passengerAddress);
        emit refund(5, balance);
    }



}
